import { createContext, useEffect} from "react";
import {useState} from "react";

export const FetchApiContext = createContext()

export const FetchApiContextProvider = ({children})=>{
  const [userEmail, setUserEmail] = useState();
  const [password, setPassword]= useState();



     /* Fetch Login.............. */
 const Login = async()=>{
  try {

    var myHeaders = new Headers();

    myHeaders.append("Content-Type", "application/json");

    var raw = JSON.stringify({
        "email": userEmail,
        "password": password
      });

    var requestOptions = {
     method: 'POST',
     headers: myHeaders,
     body: raw,
     redirect: 'follow'
    };

    fetch("http://localhost:3000/login", requestOptions)
    .then(response => response.json())
    .then(result => {localStorage.setItem("token", `Bearer ${result.token}`)})
    .catch(error => console.log('error', error));


    } catch (error) {
        console.log(error.message);
    }

 }





















 return <FetchApiContext.Provider value={{Login, userEmail, setUserEmail, password, setPassword}}>{children}
    </FetchApiContext.Provider>
}
