import styles from "../Styles/table.module.css";
import {FetchApiContext} from "../Context/FetchApiContext";
import TableMov from './TableMovements';
const { Fragment, useContext, useEffect, useState } = require("react");


function MovementsTable(){
    const{GetMovimiento,movements,totalBudget,incomes, egress,param, setParam, setHidden,setBtnEditar,setTypeLabel, setamount, setConcept, setType, setBtnAgregar} = useContext(FetchApiContext);

    useEffect(()=>{
      Mov()
    },[param])

    function Mov(){
        return(
            <div className={styles.tableMovements}>
              <div className="table-column"> <h3>Tipo</h3></div>
              <div className="table-column"> <h3>Monto</h3> </div>
              <div className="table-column"> <h3>Concepto</h3> </div>
              <div className="table-column"> <h3>Fecha</h3> </div>
              <div className="table-column"> <h3>Acciones</h3> </div>
            {param.map(mov=>{return(
             <TableMov mov={mov} key={mov.id}></TableMov>

            )})}
     </div>
        )
    }

    function reset(){
      setamount("");
      setType("");
      setConcept("");
      setHidden("");
      setBtnAgregar("")
      setBtnEditar("hidden");
      setTypeLabel("")
    }


    return(
        <Fragment>
            <div className={styles.to}>
              <div className="amount-budget"> { "$ " + totalBudget} </div>
            </div>
            <button className='button-type btn' onClick={()=>{setParam(movements);GetMovimiento()}}>ver movimientos</button>
            <button className='button-type btn' onClick={()=>{setParam(incomes)}}>ver Ingresos</button>
            <button className='button-type btn' onClick={()=>{setParam(egress)}}>ver Egresos</button>
            <button className='button-type btn' onClick={()=>{reset()}}>Agregar Movimiento</button>
           {Mov()}

        </Fragment>
    )
}

export default MovementsTable;
