import { createContext, useEffect} from "react";
import {useState} from "react";
import React from "react";
import { useNavigate } from "react-router-dom";

export const FetchApiContext = createContext()

export const FetchApiContextProvider = ({children})=>{
  const [userEmail, setUserEmail] = useState();
  const [userName, setUserName] = useState();
  const [userLastName, setUserLastName] = useState();
  const [password, setPassword]= useState();
  const [repetPassword, setRepetPassword] = useState();
  const [errorMessage, setErrorMessage] = useState();
  const [response, setResponse] =useState();
  const navigate = useNavigate();


  /* Fetch Login.............. */
  const Login = async()=>{
   const handleClick = () => {
      navigate("/home");
    }

  var myHeaders = new Headers();  
  myHeaders.append("Content-Type", "application/json");

  var raw = JSON.stringify({
      "email": userEmail,
      "password": password
    });

  var requestOptions = {
   method: 'POST',
   headers: myHeaders,
   body: raw,
   redirect: 'follow'
  };
 try {
  fetch("http://localhost:3000/login", requestOptions)

  .then(response => {
     return response
  })
  .then(result=>{
    if(result.status == 200){
      setErrorMessage("")
      result.json().then((token)=>{
        localStorage.setItem("token", `Bearer ${token}`)
        handleClick() 
      })
    }else{
      result.json().then((message)=>{
        setErrorMessage(message)
      })
    }
  })
 } catch (error) {
   setErrorMessage(error.message)}
}







 /* Fetch Registro.............. */
const SignIn = async()=>{
  const redirect = () => {
    navigate("/");
  }

  var myHeaders = new Headers();
  myHeaders.append("Authorization", localStorage.getItem("token"));
  myHeaders.append("Content-Type", "application/json");

  var raw = JSON.stringify({
    "name": userName,
    "lastName": userLastName,
    "email": userEmail,
    "password": password,
  });

  var requestOptions = {
    method: 'POST',
    headers: myHeaders,
    body: raw,
    redirect: 'follow'
  };
try { 
  fetch("http://localhost:3000/newUser", requestOptions)


  .then(response => {
    return response
 })
 .then(result=>{
   if(result.status == 200){
     setErrorMessage("")
     result.json().then((res)=>{
       setResponse(res)
     })
   }else{
     result.json().then((message)=>{
       setErrorMessage(message)
     })
   }
 })


} catch (error) {
 setErrorMessage(error.message)}

}




















 return <FetchApiContext.Provider value={{Login,
  userEmail, setUserEmail, password, setPassword,userName, setUserName,userLastName,
   setUserLastName,SignIn,repetPassword, setRepetPassword,errorMessage, setErrorMessage}}>{children}
    </FetchApiContext.Provider>
}
