import imgTacho from '../assets/trash-can-solid.svg';
import imgEdit from '../assets/pen-solid.svg';
import {FetchApiContext} from "../Context/FetchApiContext";
const { Fragment, useContext, useEffect } = require("react");


function MovementsTable(){
    const{GetMovimiento,movements,DeleteMovement, setHidden,setamount,setConcept,setType, setBtnAgregar,setIdMovement,totalBudget,incomes, egress} = useContext(FetchApiContext);
    useEffect(()=>{
     Movements()
    },[movements])
    function PreEdit(id, amount, concept, type){
        setHidden("");
        setBtnAgregar("hidden")
        setamount(amount);
        setConcept(concept);
        setType(type);
        setIdMovement(id)

    }


    console.log(incomes);
    console.log(egress)
    function Movements({array}){
        return(
            <div className="table-movements">
            {array.map(mov=>{return(
              < Fragment key={mov.id}>

              <div className="table-column"> {mov.type}</div>
              <div className="table-column"> {mov.amount}</div>
              <div className="table-column"> {mov.concept}</div>
              <div className="table-column">{mov.createdAt}</div>
              <div className ="buttnon-table-container">
                  <div className='button-table delete'onClick={()=>{DeleteMovement(mov.id)}}><img src={imgTacho} alt="delete" /></div>
                  <div className='button-table edit'onClick={()=>{PreEdit(mov.id, mov.amount, mov.concept, mov.type)}}><img src={imgEdit} alt="edit" /></div>
              </div>
              </Fragment>

            )})}
     </div>
        )
    }
    return(
        <Fragment>
             <div>{ "$ " + totalBudget}</div>
            <button onClick={()=>{GetMovimiento()}}>ver movimientos</button>
            <button onClick={()=>{GetMovimiento()}}>ver Ingresos</button>
            <button onClick={()=>{GetMovimiento()}}>ver Gastos</button>

          <Movements array></Movements>

        </Fragment>
    )
}

export default MovementsTable;
