import { createContext, useEffect} from "react";
import {useState} from "react";
import React from "react"
import { Redirect } from "react-router-dom"

export const FetchApiContext = createContext()

export const FetchApiContextProvider = ({children})=>{
  const [userEmail, setUserEmail] = useState();
  const [userName, setUserName] = useState();
  const [userLastName, setUserLastName] = useState();
  const [password, setPassword]= useState();


     /* Fetch Login.............. */
 const Login = async()=>{
  try {

    var myHeaders = new Headers();

    myHeaders.append("Content-Type", "application/json");

    var raw = JSON.stringify({
        "email": userEmail,
        "password": password
      });

    var requestOptions = {
     method: 'POST',
     headers: myHeaders,
     body: raw,
     redirect: 'follow'
    };

    fetch("http://localhost:3000/login", requestOptions)
    .then(response => response.json())
    .then(result => {localStorage.setItem("token", `Bearer ${result.token}`,)})
    .catch(error => console.log('error', error));


    } catch (error) {
        console.log(error.message);
    }

 }


 const SignIn = async()=>{
  var myHeaders = new Headers();
  myHeaders.append("Authorization", localStorage.getItem("token"));
  myHeaders.append("Content-Type", "application/json");

  var raw = JSON.stringify({
    "name": userName,
    "lastName": userLastName,
    "email": user,
    "password": "luisi123",
    "isAdmin": false
  });

  var requestOptions = {
    method: 'POST',
    headers: myHeaders,
    body: raw,
    redirect: 'follow'
  };

  fetch("http://localhost:3000/newUser", requestOptions)
    .then(response => response.text())
    .then(result => console.log(result))
    .catch(error => console.log('error', error));
 }




















 return <FetchApiContext.Provider value={{Login, userEmail, setUserEmail, password, setPassword,userName, setUserName,userLastName, setUserLastName}}>{children}
    </FetchApiContext.Provider>
}
