import { createContext, useEffect} from "react";
import {useState} from "react";
import React from "react";
import { useNavigate } from "react-router-dom";

export const FetchApiContext = createContext()

export const FetchApiContextProvider = ({children})=>{
  const [userEmail, setUserEmail] = useState();
  const [userName, setUserName] = useState();
  const [userLastName, setUserLastName] = useState();
  const [password, setPassword]= useState();
  const [repetPassword, setRepetPassword] = useState();
  const [errorMessage, setErrorMessage] = useState();
  const navigate = useNavigate();


 /* Fetch Login.............. */
 const Login = async()=>{

 const handleClick = () => {
    navigate("/home");
  }
  var myHeaders = new Headers();

  myHeaders.append("Content-Type", "application/json");

  var raw = JSON.stringify({
      "email": userEmail,
      "password": password
    });

  var requestOptions = {
   method: 'POST',
   headers: myHeaders,
   body: raw,
   redirect: 'follow'
  };
  try {


    fetch("http://localhost:3000/login", requestOptions)
    .then(response => {
       return response
    })
    .then((result) => {

      if(result.status == 200){
        setErrorMessage("")
        result.jsom.then
      }
      localStorage.setItem("token", `Bearer ${result.token}`,handleClick())})
    .catch(error => console.log('error', error));


    } catch (error) {
        console.log(error.message);
    }

 }

 /* Fetch Registro.............. */
 const SignIn = async()=>{
  const redirect = () => {
    navigate("/");}
try {
  var myHeaders = new Headers();
  myHeaders.append("Authorization", localStorage.getItem("token"));
  myHeaders.append("Content-Type", "application/json");

  var raw = JSON.stringify({
    "name": userName,
    "lastName": userLastName,
    "email": userEmail,
    "password": password,
  });

  var requestOptions = {
    method: 'POST',
    headers: myHeaders,
    body: raw,
    redirect: 'follow'
  };

  fetch("http://localhost:3000/newUser", requestOptions)
    .then(response => response.json())
    .then(result =>{redirect()} )
    .catch(error => console.log('error', error));

} catch (error) {
  console.log(error.message);
}

 }




















 return <FetchApiContext.Provider value={{Login, userEmail, setUserEmail, password, setPassword,userName, setUserName,userLastName, setUserLastName,SignIn,repetPassword, setRepetPassword,}}>{children}
    </FetchApiContext.Provider>
}
