import {FetchApiContext} from "../Context/FetchApiContext";
import TableMov from './TableMovements';
const { Fragment, useContext, useEffect, useState } = require("react");


function MovementsTable(){
    const{GetMovimiento,movements,totalBudget,incomes, egress} = useContext(FetchApiContext);
     const [param, setParam] = useState([movements])
    useEffect(()=>{
     AllMovements()
    },[movements])



    console.log(incomes);
    console.log(egress)

    function AllMovements(){
        return(
            <div className="table-movements">
            {movements.map(mov=>{return(
             <TableMov mov={mov} key={mov.id}></TableMov>

            )})}
     </div>
        )
    }
    function AllIncomes(){
        return(
            <div className="table-movements">
            {incomes.map(mov=>{return(
             <TableMov mov={mov} key={mov.id}></TableMov>

            )})}
     </div>
        )
    }
    function AllEgress(){
        return(
            <div className="table-movements">
            {egress.map(mov=>{return(
             <TableMov mov={mov} key={mov.id}></TableMov>

            )})}
     </div>
        )
    }

    function Mov(){
        return(
            <div className="table-movements">
            {egress.map(mov=>{return(
             <TableMov mov={mov} key={mov.id}></TableMov>

            )})}
     </div>
        )
    }


    return(
        <Fragment>
             <div>{ "$ " + totalBudget}</div>
            <button className='button-type' onClick={()=>{GetMovimiento()}}>ver movimientos</button>
            <button className='button-type' onClick={()=>{setParam(incomes)}}>ver Ingresos</button>
            <button className='button-type' onClick={()=>{setParam}}>ver movimientos</button>
             <div className="table-movements">
              <div className="table-column"> <h3>Tipo</h3></div>
              <div className="table-column"> <h3>Monto</h3> </div>
              <div className="table-column"> <h3>Concepto</h3> </div>
              <div className="table-column"> <h3>Fecha</h3> </div>
              <div className="table-column"> <h3>Acciones</h3> </div>
             </div>
            {AllMovements()}
            {AllIncomes()}
            {AllEgress()}

        </Fragment>
    )
}

export default MovementsTable;
